import React,{Component} from 'react';


import {
    CButton,
    CCard,
    CCardBody,
    CCardHeader,
    CCol,
    CInput,
    CLabel,
    CForm,
    CFormGroup,
    CRow,
    CSelect
  } from '@coreui/react'
  import CIcon from '@coreui/icons-react'
  import axios from 'axios';
  // summernote plugins START
  import ReactSummernote from 'react-summernote';
  import 'react-summernote/dist/react-summernote.css'; 
  import 'react-summernote/lang/summernote-ru-RU';
  // summernote plugins END

  class AddVacancy extends Component {
     state={
       editor:null,
       selected:null,
     }
    constructor(props) {
      super(props);
      this.saveNewVacancy = this.saveNewVacancy.bind(this);
    }
    handleChange = (e) => {
      let value = Array.from(e.target.selectedOptions, option => option.value);
      this.setState({
        selected: value
      });
   }
    saveNewVacancy=(e)=>{
    e.preventDefault();
    const data={
      vacancy_type:this.vacancyType,
      employee_type:this.employeeType,
      duty_station:this.dutyStation,
      experience:this.experience,
      position:this.position,
      deadline:this.deadLine,
      status:this.status,
      description:this.state.editor
    }

      console.log(this.state.selected);
    // try {
    //   axios.post('/save_vacancy',data).then(res=>{
    //     if(res.data[0].vacancy_id){

    //    console.log(res.data[0].vacancy_id)
    //     }
    //   });
    //  } catch(err){
    //   // this.handleResponseToast('OOPS! Try again',false);
    //   }
    }
    onChange=(content)=>{
     this.state.editor=content;
    }


    render() { 
      // const [collapsed, setCollapsed] = React.useState(true)
        return (
            <CRow>
            <CCol xs="12">
            <CCard>
            <CCardHeader>
              Add New
              <small> Vacancy</small>
            </CCardHeader>
            <CCardBody>
              <CForm onSubmit={this.saveNewVacancy}>
              <CRow>
              <CCol xs="6">
              <CFormGroup>
                <CLabel htmlFor="company">Duty station</CLabel>
                <CSelect onChange={this.handleChange} custom name="ccmonth" id="ccmonth" multiple size="2">
                      <option value="Head office">Head office</option>
                      <option value="Finfinne district">Finfinne district</option>
                      <option value="Asosa district">Asosa district</option>
                      <option value="Adama district">Adama district</option>
                      <option value="North district">North district</option>
                    </CSelect>
              </CFormGroup>
                </CCol>
                <CCol xs="6">
                <CFormGroup>
                <CLabel htmlFor="vat">Employee Type</CLabel>
                <CSelect onChange={(e=>this.employeeType=e.target.value)} custom name="ccmonth" id="ccmonth" required>
                      <option value="Permanent">Permanent</option>
                      <option value="Contract">Contract</option>
                    </CSelect>
                </CFormGroup>
                  </CCol>
                </CRow>
              <CRow>
              <CCol xs="6">
              <CFormGroup>
                <CLabel htmlFor="company">Vacancy type</CLabel>
                <CSelect custom onChange={(e=>this.vacancyType=e.target.value)} name="ccmonth" id="ccmonth" required>
                      <option value="">Nothing selected</option>
                      <option value="External">External</option>
                      <option value="Internal">Internal</option>
                    </CSelect>
              </CFormGroup>
                </CCol>
                <CCol xs="6">
                <CFormGroup>
                <CLabel htmlFor="vat">Experience</CLabel>
                <CInput onChange={(e=>this.experience=e.target.value)} placeholder="ex: 4 years" required />
                </CFormGroup>
                  </CCol>
                </CRow>
              <CRow>
              <CCol xs="4">
              <CFormGroup>
                <CLabel htmlFor="company">Position</CLabel>
                <CInput onChange={(e=>this.position=e.target.value)}  placeholder="ex: Manager II" required />

              </CFormGroup>
                </CCol>
                <CCol xs="4">
                <CFormGroup>
                <CLabel htmlFor="vat">DeadLine</CLabel>
                <CInput type="date" onChange={(e=>this.deadLine=e.target.value)} id="date-input" name="date-input" placeholder="date"/>
                </CFormGroup>
                  </CCol>
                <CCol xs="4">
                <CFormGroup>
                <CLabel htmlFor="vat">Status</CLabel>
                <CSelect onChange={(e=>this.status=e.target.value)} custom name="ccmonth" id="ccmonth" required>
                      <option value="Active">Active</option>
                      <option value="Pending">Pending</option>
                    </CSelect>
                </CFormGroup>
                  </CCol>
                </CRow>
              <CFormGroup>
                <CLabel htmlFor="country">General Description about vacancy </CLabel>
                <ReactSummernote
                 value="Default value"
                  onChange={this.onChange}
                  />
              </CFormGroup>
              <CButton type="submit" size="sm" color="info"><CIcon name="cil-scrubber" /> Submit</CButton> <CButton type="reset" size="sm" color="danger"><CIcon name="cil-ban" /> Reset</CButton>
            </CForm>
            </CCardBody>
          </CCard>
            </CCol>
          </CRow>
        );
    }
}

export default AddVacancy;
