import React,{Component} from 'react';
import {
    CBadge,
    CCard,
    CCardBody,
    CCardHeader,
    CCol,
    CDataTable,
    CModal,
    CModalBody,
    CModalFooter,
    CModalHeader,
    CModalTitle,
    CFormGroup,
    CInput,
    CLabel,
    CSelect,
    CForm,
    CButton,
    CRow
  } from '@coreui/react'  
  import axios from 'axios';
  import Swal from 'sweetalert2'
  import usersData from './users/UsersData'
import { Link } from 'react-router-dom';

  const getBadge = status => {
    switch (status) {
      case 'Active': return'success';
      case 'Inactive': return 'secondary'
      case 'Pending': return 'warning'
      case 'Banned': return 'danger'
      default: return 'primary'
    }
  }
  const fields = ['vacancy_name','registered', 'role', 'Action']
class SettingInstitute extends Component {
    state = {
        info:false,
        status:'Active',
        dataRows:[]
    }
    constructor(props) {
        super(props);

    }
    componentWillMount=()=>{
        try {
    axios.get('/settings/'+'Institute').then(res=>{
     this.setState({dataRows:res.data});
    });
    } catch(err) {

    }
    }
    handleSave=(e)=>{
        e.preventDefault();
        const data={
            setting_name:this.settingName,
            status:this.state.status,
            type:"Institute"
        };

        try {

            axios.post('/save_settings',data).then(res=>{
                if(res.data[0].setting_id){
                  this.handleResponseToast('New institute has added successfully!',true);
                  document.getElementById("reset-form").reset();
                  this.setState({info:false});
                  this.componentWillMount();
                }
              });
           } catch(err){
            this.handleResponseToast('OOPS! Try again',false);
            }
          }


          handleResponseToast=(msg,status)=>{
            const Toast = Swal.mixin({
              toast: true,
              position: 'top-end',
              showConfirmButton: false,
              timer: 4000,
              timerProgressBar: true,
              didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
              }
            })
            if (status) {
                Toast.fire({
              icon: 'success',
              title: msg
            })
          } else {
            Toast.fire({
              icon: 'error',
              title: msg
            })
          }
           }
    render() { 
        return (
            <>
            <CRow>
            <CCol>
              <CCard>
                <CCardHeader>
                 Setting institute information
                  <div style={{float:'right'}}>
                  <CButton className="btn btn-info btn-block btn-pill" onClick={() => this.setState({info:true})}>Add Institute</CButton>
                  </div>
                </CCardHeader>
                <CCardBody>
                <CDataTable
                  items={this.state.dataRows}
                  fields={fields}
                  hover
                  striped
                  bordered
                  size="sm"
                  itemsPerPage={10}
                  pagination
                  scopedSlots = {
                      {
                    'setting_status':
                      (item)=>(
                        <td>
                          <CBadge
                            color={getBadge(item.setting_status)}>
                            {item.setting_status}
                          </CBadge>
                        </td>
                      )
                  },{
                    'setting_date':
                      (item)=>(
                        <td>
                            {item.setting_date.split('T05:00:00.000Z')[0]}
                        </td>
                      )
                  }
                }
                />
                </CCardBody>
              </CCard>
            </CCol>

          </CRow>
           <CModal 
           show={this.state.info} 
           onClose={() => this.setState({info:false})}
           color="info"
           >
               <CForm onSubmit={this.handleSave.bind(this)} id="reset-form" >
           <CModalHeader closeButton>
             <CModalTitle>New institutes</CModalTitle>
           </CModalHeader>
           <CModalBody>
           <CFormGroup>
             <CLabel htmlFor="company">institute</CLabel>
             <CInput id="company" onChange={(e=>this.settingName=e.target.value)} style={{color:"black"}} placeholder="Enter institute name" required/>
           </CFormGroup>
           <CFormGroup>
             <CLabel htmlFor="vat">Status</CLabel>
             <CSelect style={{color:"black"}} custom onChange={(e=>this.setState({status:e.target.value}))} name="ccmonth" id="ccmonth" required>
                   <option value="Active">Active</option>
                   <option value="Pending">Pending</option>
                 </CSelect>
           </CFormGroup>

           </CModalBody>
           <CModalFooter>
             <CButton color="secondary" onClick={() => this.setState({info:false})}>Cancel</CButton>
             <CButton color="info" type="submit">Save</CButton>
           </CModalFooter>
           </CForm>
         </CModal>
         </>
        );
    }
}

export default SettingInstitute;
