import React,{Component} from 'react';
import {
    CBadge,
    CCard,
    CCardBody,
    CCardHeader,
    CCol,
    CDataTable,
    CRow
  } from '@coreui/react'  
  import axios from 'axios';
  import { Link } from 'react-router-dom';

  const getBadge = status => {
    switch (status) {
      case 'Active': return'success';
      case 'Inactive': return 'secondary'
      case 'Pending': return 'warning'
      case 'Banned': return 'danger'
      default: return 'primary'
    }
  }
  const fields = ['positions','vacancy_type', 'employee_type', 'experience','deadline']
class NewVacancy extends Component {
  state = {
    dataRows:[]
  }
    constructor(props) {
        super(props);
    }
    componentWillMount=()=>{
      try {
     axios.get('/new_vacancy').then(res=>{
      this.setState({dataRows:res.data});
     });
     } catch(err) {
     }
     }
    render() { 
        return (
            <CRow>
            <CCol>
              <CCard>
                <CCardHeader>
                  All new(not closed) vacancies
                  <div style={{float:'right'}}>
                  <Link className="btn btn-info btn-block btn-pill" to="/add_vacancy">Add New</Link>
                  </div>
                </CCardHeader>
                <CCardBody>
                <CDataTable
                  items={this.state.dataRows}
                  fields={fields}
                  hover
                  striped
                  bordered
                  size="sm"
                  itemsPerPage={10}
                  pagination
                  scopedSlots = {{
                  'deadline':
                    (item)=>(
                      <td>
                          {item.deadline.split('T05:00:00.000Z')[0]}
                      </td>
                    )
                }
              }
                />
                </CCardBody>
              </CCard>
            </CCol>
          </CRow>
        );
    }
}

export default NewVacancy;
